import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class Beers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      beers: []
    };
  }

  componentDidMount() {
    this.fetchBeers();
  }

  fetchBeers() {
    axios
      .get("https://ih-beer-api.herokuapp.com/beers")
      .then(response => {
        console.log(response.data.map(beer => beer.name));
        this.setState({
          beers: response.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <div className="App">
        {this.state.beers.map(beer => (
          <div>{beer.name}</div>
        ))}
      </div>
    );
  }
}

export default Beers;
