import React, { Component } from "react";
import axios from "axios";

const http = axios.create({
  baseURL: "https://ih-beer-api.herokuapp.com"
});

const detailBeer = id =>
  http.get(`/beers/${id}`).then(response => response.data);

class Beer extends Component {
  state = {
    Beer: []
  };

  componentDidMount() {
    const { id } = this.props.match.params;
    detailBeer
      .detailBeer(id)
      .then(response => this.setState({ Beer: response }));
  }

  render() {
    return (
      <div>
        {
          <div className="eachBeerContainer">
            <div>
              <img
                className="img-eachBeer"
                src={this.state.Beer.image_url}
                alt={this.state.Beer.name}
              />
            </div>
            <div>
              <h1>{this.state.Beer.name}</h1>
              <h2>{this.state.Beer.tagline}</h2>
              <p>{this.state.Beer.contributed_by}</p>
            </div>
          </div>
        }
      </div>
    );
  }
}

export default Beer;
