import React, { Component } from "react";
import list_countries from "../list_countries.json";
import { Link } from "react-router-dom";

const countryDetails = props => {
  const getCountry = cca3 => {
    const theCountry = oneCountry => {
      return oneCountry.cca3 === cca3;
    };
    return list_countries.find(theCountry);
  };

  const { params } = props.match;
  const foundCountry = getCountry(params.cca3, 10);

  let borders = foundCountry.borders;

  return (
    <div>
      <h2>{foundCountry.name.common}</h2>
      <h3>{foundCountry.flag}</h3>
      <p>Capital : {foundCountry.capital}</p>
      <p>Area : {foundCountry.area} km2</p>
      <p>Region : {foundCountry.region}</p>
      <p>Currency : {foundCountry.currency} </p>
      <p>Borders:</p>
      <ul>
        {foundCountry.borders.map(country => {
          return (
            <Link to={`/countries/${country}`}>
              <li>{country}</li>
            </Link>
          );
        })}
      </ul>
      <Link to="/countries">Back</Link>
    </div>
  );
};

export default countryDetails;
