import React, { Component } from "react";
import "./App.css";
import Header from "./components/Header";
import Home from "./containers/Home";
import Beers from "./containers/Beers";
import Beer from "./containers/Beer";
import NewBeer from "./containers/NewBeer";
import RandomBeer from "./containers/RandomBeer";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

class App extends Component {
  render() {
    return (
      <Router>
        <>
          <Header />
          <Switch>
            <Route exact path="/" render={props => <Home {...props} />} />
            <Route path="/Beers" render={props => <Beers {...props} />} />
            <Route
              path="/RandomBeer"
              render={props => <RandomBeer {...props} />}
            />
            <Route path="/NewBeer" render={props => <NewBeer {...props} />} />
            <Route
              exact
              path="/Beers/:_id"
              render={props => (
                <Beer {...props} findSelectedBeer={this.findSelectedBeer} />
              )}
            />
          </Switch>
        </>
      </Router>
    );
  }
}

export default App;
