import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class Beers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      beers: [],
      searchedBeers: this.props.beers
    };
  }

  componentDidMount() {
    this.fetchBeers();
  }

  fetchBeers() {
    axios
      .get("https://ih-beer-api.herokuapp.com/beers")
      .then(response => {
        this.setState({
          beers: response.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  handleChange = event => {
    let searchValue = event.target.value;
    let searchedBeers = this.state.beers.filter(beer =>
      beer.name.toLowerCase().startsWith(searchValue)
    );
    this.setState({
      searchedBeers: searchedBeers
    });
  };

  render() {
    return (
      <div className="beers">
        <div className="searchBar">
          <input type="text" onChange={this.handleChange} />
        </div>

        {this.state.beers.map(beer => {
          return (
            <Link to={`/Beers/${beer._id}`} key={beer._id} className="beer">
              <div>
                <div className="beer">
                  <div>
                    <img
                      className="beer-img"
                      src={beer.image_url}
                      alt={beer.name}
                    />
                  </div>
                  <div>
                    <h4>{beer.name}</h4>
                    <br />
                    <p>{beer.tagline}</p>
                    <br />
                    <p>
                      <small>Created by : {beer.contributed_by}</small>
                    </p>
                  </div>
                </div>
              </div>
            </Link>
          );
        })}
      </div>
    );
  }
}

export default Beers;
