import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class Beers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      beers: []
    };
  }

  componentDidMount() {
    this.fetchBeers();
  }

  fetchBeers() {
    axios
      .get("https://ih-beer-api.herokuapp.com/beers")
      .then(response => {
        this.setState({
          beers: response.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <div className="App">
        <ul>
          {this.state.beers.map(beer => (
            <Link
              to={`/Beers/${beer._id}`}
              key={beer._id}
              className="beerDetails"
            >
              {beer.name}
            </Link>
          ))}
        </ul>
      </div>
    );
  }
}

export default Beers;
