import React, { Component } from "react";
import axios from "axios";

class Beers extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      beers: {}
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    fetch("https://ih-beer-api.herokuapp.com/beers")
      .then(response => response.json())

      .then(data =>
        this.setState({
          loading: false,
          beers: data
        })
      );
  }

  render() {
    return (
      <div className="App">
        {this.state.beers.map(beer => (
          <h1>{beer.name}</h1>
        ))}
      </div>
    );
  }
}

export default Beers;
