import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class Beers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      beers: []
    };
  }

  componentDidMount() {
    this.fetchBeers();
  }

  fetchBeers() {
    axios
      .get("https://ih-beer-api.herokuapp.com/beers")
      .then(response => {
        this.setState({
          beers: response.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <div className="beers">
        {this.state.beers.map(beer => (
          <Link to={`/Beers/${beer._id}`} key={beer._id} className="beer">
            <div>
              <div className="beer">
                <div>
                  <img
                    className="beer-img"
                    src={beer.image_url}
                    alt={beer.name}
                  />
                </div>
                <div>
                  <h4>{beer.name}</h4>
                  <br />
                  <p>{beer.tagline}</p>
                  <p>{beer.contributed_by}</p>
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
    );
  }
}

export default Beers;
