import React, { Component } from "react";
import { Link } from "react-router-dom";

class Beer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      beers: []
    };
  }

  componentDidMount() {
    this.fetchBeers();
  }

  fetchBeers() {
    axios
      .get("https://ih-beer-api.herokuapp.com/beers")
      .then(response => {
        this.setState({
          beers: response.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  getBeer = _id => {
    const theBeer = oneBeer => {
      return oneBeer._id === _id;
    };
    return this.state.beers.find(theBeer);
  };

  render() {
    const { params } = props.match;
    const foundBeer = getBeer(params._id, 10);
