import React from "react";
import axios from "axios";

const Beer = props => {
  const getBeer = _id => {
    const theBeer = oneBeer => {
      return oneBeer._id === _id;
    };
    return "https://ih-beer-api.herokuapp.com/beers".find(theBeer);
  };

  const { params } = props.match;
  const foundBeer = getBeer(params._id, 10);

  return (
    <div>
      <h2>{foundBeer.name}</h2>
    </div>
  );
};

export default Beer;
